import React from 'react';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Chip from '@material-ui/core/Chip';
import Paper from '@material-ui/core/Paper';
import GitHubIcon from '@material-ui/icons/GitHub';
import Modal from '@material-ui/core/Modal';
import './Wrapper.css';

function rand() {
    return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

const useStyles = makeStyles((theme) => ({
    root: {
        width:'100%',
        display: 'flex',
        flexWrap: 'wrap',
        listStyle: 'none',
        padding: theme.spacing(0.5),
        boxShadow: 'none',
    },
    chip: {
        margin: theme.spacing(0.5),
    },
    paper: {
        position: 'absolute',
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

function Wrapper() {

    const classes = useStyles();
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState(false);
    const [chipData] = React.useState([
        { key: 0, label: 'React' },
        { key: 1, label: 'Sass' },
        { key: 2, label: 'Material-UI' },
    ]);
    const handleOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };
  return (
      <div className="wrapper">
          <p className="works">- WORKS -</p>
          <div className="margin">
              <Card className="card">
                  <CardActionArea>
                      <CardMedia
                          className="card_media"
                          image="./myportforio_20210109.png"
                          title="Contemplative Reptile"
                      />
                      <CardContent>
                          <Typography gutterBottom variant="h5" component="h2">
                              portfolio
                          </Typography>
                          <Typography variant="body2" color="textSecondary" component="p">
                              <Paper component="ul" className={classes.root}>
                                  {chipData.map((data) => {
                                      return (
                                          <li key={data.key}>
                                              <Chip
                                                  label={data.label}
                                                  className={classes.chip}
                                              />
                                          </li>
                                      );
                                  })}
                              </Paper>
                          </Typography>
                      </CardContent>
                  </CardActionArea>
                  <CardActions>
                      <Button size="small" color="LINK">
                          <GitHubIcon />
                          <p>code view</p>
                      </Button>

                      <Button size="small" color="primary">
                          Learn More
                      </Button>
                  </CardActions>
              </Card>
          </div>
          <div className="skill-margin">
              <p className="skill">- SKILL -</p>
              <p>- OS: macOS</p>
              <p>- DB: MySQL</p>
              <p>- SCM: Git</p>
              <p>- 言語: Java 8 / PHP7系 / Ruby2.6</p>
              <p>- FW: Play 2 Framework / Laravel6 / Ruby on Rails5 / Spring Boot</p>
              <p>- UI-FW: Bootstrap / JavaScript / TypeScript / jQuery / React.js / Redux / webpack 4</p>
              <p>- INFRA: AWS / VPS / Docker</p>
          </div>
      </div>
  );
}

export default Wrapper;
