import React from 'react';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Chip from '@material-ui/core/Chip';
import Paper from '@material-ui/core/Paper';
import TagFacesIcon from '@material-ui/icons/TagFaces';
import './Wrapper.css';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        justifyContent: 'center',
        flexWrap: 'wrap',
        listStyle: 'none',
        padding: theme.spacing(0.5),
        margin: 0,
    },
    chip: {
        margin: theme.spacing(0.5),
    },
}));

function Wrapper() {

    const classes = useStyles();
    const [chipData, setChipData] = React.useState([
        { key: 0, label: 'Angular' },
        { key: 1, label: 'jQuery' },
        { key: 2, label: 'Polymer' },
        { key: 3, label: 'React' },
        { key: 4, label: 'Vue.js' },
    ]);

    const handleDelete = (chipToDelete) => () => {
        setChipData((chips) => chips.filter((chip) => chip.key !== chipToDelete.key));
    };

  return (
      <div className="wrapper">
          <p className="works">- WORKS -</p>
          <div className="margin">
              <Card className="card">
                  <CardActionArea>
                      <CardMedia
                          className="card_media"
                          image="./myportforio_20210109.png"
                          title="Contemplative Reptile"
                      />
                      <CardContent>
                          <Typography gutterBottom variant="h5" component="h2">
                              portfolio
                          </Typography>
                          <Typography variant="body2" color="textSecondary" component="p">
                              skill:React,Sass,Material-UI
                          </Typography>
                      </CardContent>
                  </CardActionArea>
                  <CardActions>
                      <Button size="small" color="primary">
                          Share
                      </Button>
                      <Button size="small" color="primary">
                          Learn More
                      </Button>
                  </CardActions>
              </Card>
          </div>
          <div className="skill-margin">
              <p className="skill">- SKILL -</p>
              <p>- OS: macOS</p>
              <p>- DB: MySQL</p>
              <p>- SCM: Git</p>
              <p>- 言語: Java 8 / PHP7系 / Ruby2.6</p>
              <p>- FW: Play 2 Framework / Laravel6 / Ruby on Rails5 / Spring Boot</p>
              <p>- UI-FW: Bootstrap / JavaScript / TypeScript / jQuery / React.js / Redux / webpack 4</p>
              <p>- INFRA: AWS / VPS / Docker</p>
          </div>
      </div>
  );
}

export default Wrapper;
